#include <vector>
#include <utility>
#include <climits>
#include <algorithm>

using namespace std;

vector<pair<int,int>> subarray(vector<int> A, int *value) {
    int n = A.size();

    vector<int> B(n); // Transforming the array to further simplify the problem
    for (int i = 0; i < n; i++) {
        B[i] = (i % 2 ? 1 : -1) * A[i];
    }

    // Start of the Kadane's Algorithm Logic
    vector<int> dp(n);
    vector<int> start_indices(n);
    dp[0] = B[0];
    start_indices[0] = 1;
    int max_val = dp[0];
    int index = 0;

    for (int i = 1; i < n; ++i) {
        if (dp[i - 1] >= 0) {
            dp[i] = dp[i - 1] + B[i];
            start_indices[i] = start_indices[i - 1];
        } else {
            dp[i] = B[i];
            start_indices[i] = i + 1;
        }

        if (dp[i] > max_val) {
            max_val = dp[i];
            index = i;
        }
    }
    // End of the Kadane's Algorithm Logic

    int end_index = index + 1;
    int start_index = start_indices[index];

    // Extracting pairs resulting in highest non-zero negative value
    vector<pair<int, int>> result;
    *value = max_val;
    for (int i = start_index; i <= end_index; ++i) {
        result.push_back(make_pair(start_index, i));
    }

    return result;
}

